import React, { useState, useEffect } from "react";
import YourBotArmy from "./Yourbot";
import BotsPage from "./BotsPage";


const botsAvailable = 'http://localhost:3000/bots'



function BotCollection() {

  const [bots, setBots] = useState([])


  useEffect(() => {
    fetch(botsAvailable)
      .then(response => response.json())
      .then(data => setBots(data))
  }, [])

  const DeleteBot = (bot) => {
    fetch(`botsAvailable/${bot.id}`, {
      method: "DELETE",
      header: { "Content-Type": "application/json" }
    }).then((result) => {
      result.json().then((resp) => {
        console.warn(resp)
      })
    });
  }

  function display(bot) {
    if (army.includes(bot));
    setArmy((army) => [...army, bot]);
  }

  function remove(bot) {
    setArmy((army) => army.filter((item) => item.id !== bot.id));
  }

  return (
    <div className="bot-collection" >
      <YourBotArmy BotDisplay={army} handleDisplay={remove} DeleteBot={DeleteBot} />
      <BotsPage BotArr={bots} handleDisplay={display} DeleteBot={DeleteBot} />
    </div>
  )
}

export default BotCollection;
